# TODO: Comment this, so it makes sense and is not a mess
def getClassData(link):

    # Selenium config
    s = Service(ChromeDriverManager().install())
    options = Options()
    options.headless = True
    driver = webdriver.Chrome(service=s, options=options)
    driver.get(link)

    # get innermost element that still contains all the data.
    html = driver.execute_script('''return document.querySelector("ucla-sa-soc-app").shadowRoot.getElementById("class_detail")''')
    # grab all the important info
    subject = html.find_element(By.ID, "subject_class").get_attribute('innerHTML')
    description = html.find_element(By.ID, "section").text
    description = description[:description.find("Department")]
    courseCode = (subject.split("<br>")[1]).split("</p>")[0]
    courseCode = " ".join(courseCode.split())
    fullName = courseCode.split("-")[1].lstrip()
    abbreviation = " ".join(courseCode.split("-")[0].split()[:-1])
    courseCode = courseCode.split(" - ")[0]
    
    subjectArea = subject[4:subject.find("(") - 1]
    restrictions = html.find_elements(By.CLASS_NAME, "enrollment_info")[1].find_elements(By.TAG_NAME, "td")[1].text
    units = html.find_elements(By.CLASS_NAME, "enrl_mtng_info")[1].find_elements(By.TAG_NAME, "td")[5].text
    requisites = ""
    if (html.find_element(By.ID, "course_requisites").text != ""):
        requisites = html.find_elements(By.CLASS_NAME, "course_requisites")
    isOr = []
    currentQuarter = link[link.find("term_cd") + 8:link.find("term_cd") + 11]
    enforcedPrereqs = []
    optionalPrereqs = []
    enforcedCoreqs = []

    # don't think too much about it. It works, I think, and probably doesn't break. 
    for i, val in enumerate(requisites[1:]):
        requisite = val.find_element(By.CLASS_NAME, "popover-right").text.strip(" and")
        if "or" in requisite:
            isOr.append(True)
        if (len(isOr) != 0 and isOr[i - 1] == True and i == len(isOr) - 1):
            isOr.append(True)
        else:
            isOr.append(False)
        requisite = requisite.strip(" or")
        isEnforcedCoreq = True if val.find_elements(By.TAG_NAME, "td")[3].text == "Yes" else False
        if (isEnforcedCoreq):
            enforcedCoreqs.append(requisite)
            continue
        isEnforcedPrereq = True if val.find_elements(By.TAG_NAME, "td")[2].text == "Yes" and not isOr[i] else False
        if (isEnforcedPrereq) :
            enforcedPrereqs.append(requisite)
            continue
        isOptionalPrereq = True if val.find_elements(By.TAG_NAME, "td")[2].text == "Yes" and isOr[i] else False
        if (isOptionalPrereq):
            optionalPrereqs.append(requisite)
            continue
    # add all the objects to our overall class object and return it to the calling function.
    Class = {"courseCode": courseCode, "name": fullName, "subjectArea": subjectArea, "subjectAreaAbbreviation": abbreviation, "quarterOffered": currentQuarter, "units": units, "enforcedPrerequisites": enforcedPrereqs, "optionalPrerequisites": optionalPrereqs, "enforcedCorequisites": enforcedCoreqs, "description": description, "restrictions": restrictions}
    driver.close()
    return Class

